import {useState, useEffect } from 'react';

import Button from './Button'

function Contacts(){ 
    const [first_name, setFirst_name] = useState('');
    const [last_name, setLast_name] = useState('');
    const [phone_number, setPhone_number] = useState('');
    const [email, setEmail] = useState('');
    const [id, setId] = useState('');

    const [data, setData] = useState([]);

    var success = true;

    useEffect(() => {
        getContacts();
    },[]);


    async function getContacts(){
        var userID = localStorage.getItem("userID");

        const res = await fetch('http://localhost:3001/contacts/' + userID, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            },
        });

        var status = await res.status;
        var data = null;

        if(status === 201 || status === 200){
            data = await res.json();
        } 
        else{
            success = false;
        }

        setData(data);
    }

    async function addContact(e){
        var userID = localStorage.getItem("userID");

        const res = await fetch('http://localhost:3001/contacts/', {
            method: 'POST',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                id: userID,
                first_name: first_name,
                last_name: last_name,
                phone_number: phone_number,
                email: email
            })
        });

        var status = await res.status;
        var data = null;

        if(status === 201){
            data = await res.json();
        } 
        else{
            success = false;
        }

        setFirst_name("");
        setLast_name("");
        setPhone_number("");
        setEmail("");
 
        window.location.href = "/contact_list";
    }

    return (
        <>
            <form className="form-group " onSubmit={addContact}>
                    <h2 style={{marginBottom: "30px"}}> Add contact </h2>
                    <input
                        style={{marginBottom: "15px"}}
                        type="text"
                        className="form-control"
                        name="first_name"
                        placeholder="First name"
                        value={first_name}
                        onChange={(e) => {
                        setFirst_name(e.target.value);
                        }}
                    />
                    <input
                        style={{marginBottom: "15px"}}
                        type="text"
                        className="form-control"
                        name="last_name"
                        placeholder="Last name"
                        value={last_name}
                        onChange={(e) => {
                        setLast_name(e.target.value);
                        }}
                    />
                    <input
                        style={{marginBottom: "15px"}}
                        type="text"
                        className="form-control"
                        name="phone_number"
                        placeholder="Phone number"
                        value={phone_number}
                        onChange={(e) => {
                        setPhone_number(e.target.value);
                        }}
                    />
                    <input
                        style={{marginBottom: "15px"}}
                        type="text"
                        className="form-control"
                        name="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => {
                        setEmail(e.target.value);
                        }}
                    />
                    <Button text="Add contact"/>    
            </form>

            <div>
                <p>
                {
                    data.map(contact=>{
                        return(
                        <span key={contact._id}>
                            <label className="name lead">{contact.first_name}{" "}{contact.last_name}</label><br/>
                            <span class="text-muted small">{contact.phone_number}</span><br/>
                            <span class="text-muted small">{contact.email}</span><br/><br/>
                        </span>)
                    })
                }
                </p>
            </div>
        </>
    );
}

export default Contacts;
